s = input()
sc = Counter(s)
odd_found = False
odd_chr   = ""
half = ""
odd_chr_cnt = 0
can_not = False
for values in sc.items():
    
    if values[1]%2==1:
        if odd_found:
            can_not = True
            break
        else:
            odd_found+=1
            odd_chr = values[0]
            odd_chr_cnt = values[1]
    else:
        half += values[0] * (values[1]//2 )
if can_not:
    print(-1)
else:
    half1 = sorted(half + odd_chr * ((odd_chr_cnt-1) // 2))     
    half2 = half1[::-1]
    half = "".join(half1)+odd_chr+"".join(half2)
    print(half)  

